#!/usr/bin/env node

'use strict';

var spawn = require('child_process').spawn,
	Promise = require('bluebird'),
	fs = Promise.promisifyAll(require('fs-extra')),
	EventEmitter = require('events');

class Executor extends EventEmitter {

	constructor() {
		super();

		var depPath = '.';

		this._depPath = (path) => {
			var old = depPath;
			depPath = path;
			return old;
		};

		this._target = (stage, prev, action) => {
			this[stage] = () => {
				var guard = `${depPath}/.dep/${stage}`;
				if (fs.existsSync(guard)) {
					return Promise.resolve();
				} else {
					var before = prev ? this[prev].bind(this) : Promise.resolve;
					var task = () => {
						this.emit('targetStart', stage);
						return action();
					};
					var after = (arg) => {
						this.emit('targetFinish', stage);
						return stage === 'run' ? Promise.resolve(arg) : fs.outputFileAsync(guard, '');
					};
					return before().then(task).then(after);
				}
			}
		}

		this._run = (cmd, args, opts) => {
			console.log(cmd + ' ' + args.join(' '));
			return new Promise((resolve, reject) => {
				var stdout = '', stderr = '';
				var child = spawn(cmd, args, opts);
				child.stdout.on('data', (data) => {
					stdout += data;
					this.emit('stdout', data);
				});
				child.stderr.on('data', (data) => {
					stderr += data
					this.emit('stderr', data);
				});
				child.on('exit', (status) => {
					if (status) {
						reject({stdout, stderr, status});
					} else {
						resolve({stdout, stderr, status: 0});
					}
				});
			});
		}

		this._runWatch = (cmd, args, opts, match) => {
			console.log(cmd + ' ' + args.join(' '));
			return new Promise((resolve, reject) => {
				var matched = false;
				var stdout = '', stderr = '';
				var child = spawn(cmd, args, opts);
				child.stdout.on('data', (data) => {
					stdout += data;
					this.emit('stdout', data);
				});
				child.stderr.on('data', (data) => {
					stderr += data;
					this.emit('stderr', data);
					if (!matched && stderr.match(match)) {
						matched = true;
						resolve({stdout, stderr, status: 0});
					}
				});
				child.on('exit', (status) => {
					if (matched) {
						// do nothing
					} else {
						reject({stdout, stderr, status});
					}
				});
			});
		}
	}
}

module.exports = Executor;
